import React, { FC } from 'react';
import ContactRow from './ContactRow';
import { Contact, SortField } from '../utils/types';
import style from './ContactsList.module.scss'
import { sortContacts } from '../utils/contactsUtils';

export interface ContactsListProps {
  contacts: Contact[];
  editingId: string;
  editContact: (id: string) => void;
  saveContact: (contact: Contact) => void;
  deleteContact: (id: string) => void;
  sortField: SortField;
}

const ContactsList: FC<ContactsListProps> = ({ contacts, editingId, editContact, saveContact, deleteContact, sortField }) => {

  return (
    <div className={style.container}>
      {contacts.map((contact) => (
        <ContactRow
          key={contact.id}
          contactData={contact}
          isEditing={editingId === contact.id}
          onEdit={() => editContact(contact.id)}
          onSave={saveContact}
          onDelete={() => deleteContact(contact.id)}
        />
      ))}
    </div>
  );
};

export default ContactsList;
