import React, { FC } from "react";
import { LabelsKey, SortField } from "../utils/types";
import SortArrow from "./SortArrow";
import style from './Headline.module.scss'
import { LABELS } from "../utils/constants";

interface HeadlineProps {
  label: LabelsKey;
  currentSortField: SortField;
  onClick: () => void;
}

const Headline: FC<HeadlineProps> = ({ label, currentSortField, onClick }) => {
  return (
    <div onClick={onClick} className={style[label]}>
      {LABELS[label]}
      {currentSortField.name === label && <SortArrow direction={currentSortField.direction} />}
    </div>
  );
};

export default Headline
