import { Contact, SortField } from "./types";

export const getContacts = (): Contact[] => {
  const contacts = localStorage.getItem('contacts');
  return contacts ? JSON.parse(contacts) : [];
};

export const saveToLocalStorage = (contacts: Contact[]): void => {
  localStorage.setItem('contacts', JSON.stringify(contacts));
};

export const sortContacts = (contacts: Contact[], sortField: SortField): Contact[] => {
  return [...contacts].sort((a, b) => {
    let comparison = a[sortField.name].toLowerCase().localeCompare(b[sortField.name].toLowerCase());
    return sortField.direction === 'asc' ? comparison : -comparison;
  });
};
